mapper.select:
SELECT 

FROM tp_car a

WHERE a.id = #{id}

SELECT
FROM tp_car a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.deviceid LIKE '%'||#{deviceId}||'%' '%'+#{deviceId}+'%' concat('%',#{deviceId},'%')
AND a.office_id = #{office.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.brand LIKE '%'||#{brand}||'%' '%'+#{brand}+'%' concat('%',#{brand},'%')
AND a.purpose LIKE '%'||#{purpose}||'%' '%'+#{purpose}+'%' concat('%',#{purpose},'%')
AND a.person_count BETWEEN #{beginPersonCount} AND #{endPersonCount}
AND a.carrying_capacity BETWEEN #{beginCarryingCapacity} AND #{endCarryingCapacity}
AND a.remarks LIKE '%'||#{remarks}||'%' '%'+#{remarks}+'%' concat('%',#{remarks},'%')
AND a.location_name LIKE '%'||#{locationName}||'%' '%'+#{locationName}+'%' concat('%',#{locationName},'%')
AND a.start_km BETWEEN #{beginStartKm} AND #{endStartKm}
AND a.sum_km BETWEEN #{beginSumKm} AND #{endSumKm}
AND a.current_km BETWEEN #{beginCurrentKm} AND #{endCurrentKm}
AND a.sum_time BETWEEN #{beginSumTime} AND #{endSumTime}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tp_car a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tp_car SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
