mapper.select:
SELECT 

FROM tp_guardrail a

WHERE a.id = #{id}

SELECT
FROM tp_guardrail a
a.del_flag = #{DEL_FLAG_NORMAL} ${dataScope}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.office_id = #{office.id}
AND a.price BETWEEN #{beginPrice} AND #{endPrice}
AND a.standards LIKE '%'||#{standards}||'%' '%'+#{standards}+'%' concat('%',#{standards},'%')
AND a.remarks LIKE '%'||#{remarks}||'%' '%'+#{remarks}+'%' concat('%',#{remarks},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tp_guardrail a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tp_guardrail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
