mapper.select:
SELECT 

FROM tp_road_crossing a

WHERE a.id = #{id}

SELECT 

FROM tp_road_crossing a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tp_road_crossing a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tp_road_crossing SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
